#!/bin/bash

#make sure all output directories are present
cat outputs_folder.list | xargs mkdir > /dev/null 2>&1

#STS 2014 (task10) simulation with DBPedia lookups:
#---Training
#for *OnWN:
java7 -Xmx3g -jar dist/ASAP-Project.jar -i "input/STS.input.OnWN.2012.txt;input/STS.gs.OnWN.2012.txt" -i "input/STS.input.OnWN.2013.txt;input/STS.gs.OnWN.2013.txt" -ll -lg -lt -lflf "outputs/2014-task10/simulation2/OnWN/LemmasFoundInDBP-train.log" -lpef "outputs/2014-task10/simulation2/OnWN/errors/training.log" -mt 16 -o "outputs/2014-task10/simulation2/OnWN/predictions/STS.OnWN.txt" -mo "outputs/2014-task10/simulation2/weka-models-OnWN" -fo "outputs/2014-task10/simulation2/OnWN/features-sts2014-OnWN-train.txt" -tof "outputs/2014-task10/simulation2/OnWN/preprocessed-OnWN-train.txt" -llf "outputs/2014-task10/simulation2/OnWN/LemmasNotFound-OnWN-train.log" -lgf "outputs/2014-task10/simulation2/OnWN/GrammarCounters-OnWN-train.log" -lt -ltf "outputs/2014-task10/simulation2/OnWN/Timings-OnWN-train.log" -lner -lnerf "outputs/2014-task10/simulation2/OnWN/NamedEntitiesFound-OnWN-train.log" | tee outputs/2014-task10/simulation2/run-OnWN-train.log

# *headlines:
java7 -Xmx3g -jar dist/ASAP-Project.jar -i "input/STS.input.headlines.2013.txt;input/STS.gs.headlines.2013.txt" -ll -lg -lt -lflf "outputs/2014-task10/simulation2/headlines/LemmasFoundInDBP-headlines-train.log" -lpef "outputs/2014-task10/simulation2/headlines/errors/training.log" -mt 16 -o "outputs/2014-task10/simulation2/headlines/predictions/STS.headlines-train.txt" -mo "outputs/2014-task10/simulation2/weka-models-headlines" -fo "outputs/2014-task10/simulation2/headlines/features-sts2014-headlines-train.txt" -tof "outputs/2014-task10/simulation2/headlines/preprocessed-headlines-train.txt" -llf "outputs/2014-task10/simulation2/headlines/LemmasNotFound-headlines-train.log" -lgf "outputs/2014-task10/simulation2/headlines/GrammarCounters-headlines-train.log" -lt -ltf "outputs/2014-task10/simulation2/headlines/Timings-headlines-train.log" -lner -lnerf "outputs/2014-task10/simulation2/headlines/NamedEntitiesFound-headlines-train.log" | tee outputs/2014-task10/simulation2/run-headlines-train.log

# *deft-news trained with SMTnews
